import {
  Box,
  Button,
  Card,
  CardActions,
  CardContent,
  Typography,
} from '@mui/material';
import { createStyles, makeStyles } from '@mui/styles';
import React, { ReactElement, useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { getData } from '../data';
import { date, patientName, sex } from '../util';

// Uncomment to use the mock api calls.
// import * as api from '../mocks/api';
// TODO: get useEffect
// Grab ID from URL
// Make api call to get the same list /id
// display it on the page with a different render method

const useStyles = makeStyles((theme: any) =>
  createStyles({
    header: {
      fontSize: '14',
      color: 'gray',
    },
    text: {
      fontSize: '18',
      color: '#001D3F',
    },
    container: {
      border: '1px solid',
      color: '#001D3F',
      minWidth: 275,
    },
  })
);

const ViewPatient = (): ReactElement => {
  const [data, setData] = useState([]);

  useEffect(() => {
    getData().then((data: any) => {
      setData(data);
    });
  }, []);

  const url = window.location.href;
  const splitUrl: string[] = url.split('/');
  const id: number = parseInt(splitUrl[splitUrl.length - 1]);
  const patient: any = data[id - 1];
  const classes = useStyles();

  if (data.length == 0) return <div></div>;

  const card = (
    <React.Fragment>
      <CardActions>
        <Button size="small">
          <Link to={'/patients'}>Go back to Patient List</Link>
        </Button>
      </CardActions>
      <CardContent>
        <Typography className={classes.header} gutterBottom>
          Patient
        </Typography>
        <Typography className={classes.text}>
          {patientName(patient.first_name, patient.last_name)}
        </Typography>
        <Typography className={classes.header} gutterBottom>
          Sex
        </Typography>
        <Typography className={classes.text}>{sex(patient.sex)}</Typography>
        <Typography className={classes.header} gutterBottom>
          Email
        </Typography>
        <Typography className={classes.text}>
          <a href={`mailto:${patient.email}`}>{patient.email}</a>
        </Typography>
        <Typography className={classes.header} gutterBottom>
          Created at:
        </Typography>
        <Typography className={classes.text}>
          {date(patient.created_at)}
        </Typography>
        <Typography className={classes.header} gutterBottom>
          Updated at:
        </Typography>
        <Typography className={classes.text}>
          {date(patient.updated_at)}
        </Typography>
      </CardContent>
    </React.Fragment>
  ) || <div></div>;

  return (
    <Box className={classes.container}>
      <Card variant="outlined">{card}</Card>
    </Box>
  );
};

export default ViewPatient;
